sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var checkWhenNeeded = new GlideAjax('isDateToday');
	checkWhenNeeded.addParam('sysparm_name','checkDate');
	checkWhenNeeded.addParam('sysparm_userID',g_form.getValue('u_when_needed'));

	checkWhenNeeded.getXML(checkDate);

	function checkDate(response){
		var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		if(!emailFromScriptInclude)
			g_form.addInfoMessage("Date Entered is todays Date.Please enter another date");

	}

}
