sys_script_include.script:
var isDateToday = Class.create();
isDateToday.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	checkDate: function(strDate){
		var currentDate = new GlideDateTime();
		var isCurrentDate = new GlideDateTime(strDate);

		//Local Date and Time
		var today = currentDate.getLocalDate();
		var isToday = isCurrentDate.getLocalDate();

		if(today.before(isToday))
			return true;
		else
			return false;
	},
    type: 'isDateToday'
});
